// Get form data
const formData = $input.first().json;
const jobDescription = formData['Job Description'] || '';

// Get current date using Luxon
const now = DateTime.now();
const dateISO = now.toISODate();
const dateFormatted = now.toFormat('MMMM d, yyyy');
const timestamp = now.toISO();

// Auto-extract information from job description
const lines = jobDescription.split('\n').filter(l => l.trim());
let companyName = 'Unknown';
let jobTitle = 'Unknown';
let location = '';
let salaryRange = '';

// Extract from first few lines
for (let i = 0; i < Math.min(10, lines.length); i++) {
  const line = lines[i].trim();

  // Company name detection
  if (i < 3 && line.length > 2 && line.length < 100) {
    if (line.match(/\b(company|inc\.|ltd\.|llc|corp\.?)\b/i)) {
      companyName = line;
    }
  }

  // Job title (usually first line)
  if (i === 0 && line.length > 2 && jobTitle === 'Unknown') {
    jobTitle = line;
  }

  // Location
  if (line.match(/location:/i)) {
    location = line.replace(/location:/i, '').trim();
  }

  // Salary
  if (line.match(/\$[\d,]+|salary/i)) {
    salaryRange = line.substring(0, 100);
  }
}

// Return formatted record for Google Sheets
return [{
  json: {
    dateSubmitted: dateISO,
    dateFormatted: dateFormatted,
    timestamp: timestamp,
    companyName: companyName,
    jobTitle: jobTitle,
    location: location,
    salaryRange: salaryRange,
    jobUrl: '',
    jobDescription: jobDescription,
    status: 'New',
    notes: ''
  }
}];
